INSERT INTO `zl_wc_admin_notes` (`note_id`, `name`, `type`, `locale`, `title`, `content`, `content_data`, `status`, `source`, `date_created`, `date_reminder`, `is_snoozable`, `layout`, `image`, `is_deleted`, `icon`) VALUES
(1, 'wc-admin-onboarding-email-marketing', 'info', 'en_US', 'Tips, pembaruan produk, dan inspirasi', 'Kami di sini untuk Anda - dapatkan tips, pembaruan produk, dan inspirasi di kotak email Anda', '{}', 'unactioned', 'woocommerce-admin', '2020-09-03 05:57:38', NULL, 0, 'plain', '', 0, 'info'),
(2, 'wc-admin-marketing-intro', 'info', 'en_US', 'Terhubung dengan audiens Anda', 'Kembangkan basis pelanggan dan tingkatkan penjualan dengan alat pemasaran yang dibuat untuk WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2020-09-03 05:57:39', NULL, 0, 'plain', '', 0, 'info'),
(3, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Sambungkan ke WooCommerce.com', 'Sambungkan untuk mendapatkan pemberitahuan dan pembaruan produk penting.', '{}', 'unactioned', 'woocommerce-admin', '2020-09-03 05:57:39', NULL, 0, 'plain', '', 0, 'info'),
(4, 'wc-admin-mobile-app', 'info', 'en_US', 'Instal aplikasi seluler Woo', 'Instal aplikasi seluler WooCommerce untuk mengelola pesanan, menerima pemberitahuan penjualan, dan melihat metrik kunci  di mana pun Anda berada.', '{}', 'unactioned', 'woocommerce-admin', '2020-09-05 06:25:25', NULL, 0, 'plain', '', 0, 'info'),
(5, 'wc-admin-coupon-page-moved', 'update', 'en_US', 'Manajemen kupon telah dipindahkan!', 'Kupon kini bisa dikelola dari Pemasaran &gt; Kupon. Klik tombol di bawah ini untuk menghapus WooCommerce legasi &gt; Item menu kupon.', '{}', 'unactioned', 'woocommerce-admin', '2020-09-05 06:37:40', NULL, 0, 'plain', '', 0, 'info'),
(6, 'wc-admin-onboarding-profiler-reminder', 'update', 'en_US', 'Selamat datang di WooCommerce! Siapkan toko Anda dan mulai menjual', 'Kami di sini untuk membantu Anda melewati langkah terpenting untuk menyiapkan dan menjalankan toko Anda.', '{}', 'actioned', 'woocommerce-admin', '2020-09-05 07:46:29', NULL, 0, 'plain', '', 0, 'info'),
(7, 'wc-admin-draw-attention', 'info', 'en_US', 'Cara menarik perhatian ke toko online Anda', 'Untuk memulai, berikut adalah tujuh cara untuk meningkatkan penjualan Anda dan menghindari agar Anda tidak menghabiskan waktu untuk menjual produk serupa yang diproduksi secara massal dan menargetkan pembeli yang sama.', '{}', 'unactioned', 'woocommerce-admin', '2020-09-05 07:48:11', NULL, 0, 'plain', '', 0, 'info'),
(8, 'ecomm-need-help-setting-up-your-store', 'info', 'en_US', 'Need help setting up your Store?', 'Schedule a free 30-min <a href=\"https://wordpress.com/support/concierge-support/\">quick start session</a> and get help from our specialists. Were happy to walk through setup steps, show you around the WordPress.com dashboard, troubleshoot any issues you may have, and help you the find the features you need to accomplish your goals for your site.', '{}', 'pending', 'woocommerce.com', '2020-09-05 07:49:14', NULL, 0, 'plain', '', 0, 'info'),
(9, 'woocommerce-services', 'info', 'en_US', 'WooCommerce Services', 'WooCommerce Services helps get your store ready to sell as quickly as possible. You create your products. We take care of tax calculation, payment processing, and shipping label printing! Learn more about the extension that you just installed.', '{}', 'pending', 'woocommerce.com', '2020-09-05 07:49:14', NULL, 0, 'plain', '', 0, 'info'),
(10, 'ecomm-unique-shopping-experience', 'info', 'en_US', 'For a shopping experience as unique as your customers', 'Product Add-Ons allow your customers to personalize products while theyre shopping on your online store. No more follow-up email requestscustomers get what they want, before theyre done checking out. Learn more about this extension that comes included in your plan.', '{}', 'pending', 'woocommerce.com', '2020-09-05 07:49:14', NULL, 0, 'plain', '', 0, 'info'),
(11, 'wc-admin-start-dropshipping-business', 'info', 'en_US', 'Apakah Anda sedang mempertimbangkan untuk memulai usaha dropshipping?', 'Kemampuan untuk menambah persediaan tanpa harus mengurus produksi, pengisian stok, atau pemenuhan pesanan mungkin terdengar seperti mimpi. Namun apakah dropshipping menguntungkan? Mari pelajari beberapa kelebihan dan kekurangannya untuk membantu Anda mengambil keputusan terbaik bagi bisnis Anda.', '{}', 'unactioned', 'woocommerce-admin', '2020-09-07 03:11:13', NULL, 0, 'plain', '', 0, 'info'),
(12, 'wc-admin-learn-more-about-product-settings', 'info', 'en_US', 'Pelajari lebih lanjut mengenai Pengaturan Produk', 'Di video ini, Anda akan menemukan informasi mengenai cara mengonfigurasi pengaturan produk, seperti tampilan produk, mengedit pilihan persediaan, dan masih banyak lagi.', '{}', 'unactioned', 'woocommerce-admin', '2020-09-07 03:11:13', NULL, 0, 'plain', '', 0, 'info'),
(13, 'wc-admin-online-clothing-store', 'info', 'en_US', 'Mulai toko pakaian online Anda', 'Memulai situs web mode sangat menarik, tetapi juga terlihat cukup menantang. Dalam artikel ini, kami akan memandu Anda selama proses pengaturan, mengajarkan Anda cara membuat daftar produk yang baik, dan menunjukkan cara melakukan pemasaran ke target audiens Anda.', '{}', 'unactioned', 'woocommerce-admin', '2020-09-07 03:11:13', NULL, 0, 'plain', '', 0, 'info'),
(14, 'wc-admin-choose-niche', 'info', 'en_US', 'Cara memilih celah bisnis untuk toko online Anda', 'Celah bisnis menentukan produk dan jasa yang Anda kembangkan. Celah bisnis mengarahkan pemasaran Anda. Celah bisnis memfokuskan perhatian Anda pada masalah spesifik yang dihadapi pelanggan Anda. Celah bisnis membedakan Anda dari para pesaing. Pelajari lebih lanjut mengenai lima prinsip dasar untuk mengetahui celah bisnis Anda.', '{}', 'unactioned', 'woocommerce-admin', '2020-09-07 03:11:13', NULL, 0, 'plain', '', 0, 'info'),
(15, 'wc-admin-launch-checklist', 'info', 'en_US', 'Siap meluncurkan toko Anda?', 'Untuk memastikan Anda tidak merasa melupakan sesuatu, kami telah menyusun daftar periksa pra-peluncuran yang penting.', '{}', 'unactioned', 'woocommerce-admin', '2020-09-07 05:59:34', NULL, 0, 'plain', '', 0, 'info'),
(16, 'wc-admin-need-some-inspiration', 'info', 'en_US', 'Perlu inspirasi?', 'Lihat beberapa cerita pelanggan favorit kami dari pengusaha, agensi, hingga pengembang dalam komunitas global kami.', '{}', 'unactioned', 'woocommerce-admin', '2020-09-09 03:02:47', NULL, 0, 'plain', '', 0, 'info'),
(17, 'wc-update-db-reminder', 'update', 'en_US', 'Pembaruan database WooCommerce diperlukan', 'WooCommerce telah diperbarui! Agar segalanya berjalan lancar, kami harus memperbarui database Anda ke versi terbaru. Pembaruan database berjalan di latar belakang dan mungkin memerlukan waktu beberapa saat, harap bersabar. Pengguna tingkat lanjut dapat melakukan pembaruan dengan cara alternatif melalui <a href=\"https://github.com/woocommerce/woocommerce/wiki/Upgrading-the-database-using-WP-CLI\">WP CLI</a>.', '{}', 'unactioned', 'woocommerce-core', '2020-08-30 20:37:05', NULL, 0, 'plain', '', 0, 'info'),
(18, 'wc-admin-usage-tracking-opt-in', 'info', 'en_US', 'Bantu WooCommerce menjadi lebih baik dengan pelacakan penggunaan', 'Mengumpulkan data penggunaan memungkinkan kami meningkatkan WooCommerce. Toko Anda akan dipertimbangkan selagi kami mengevaluasi fitur baru, menilai kualitas pembaruan, atau menentukan apakah sebuah pembaruan masuk akal. Anda selalu dapat mengunjungi <a href=\"http://localhost/zakiyah/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Pengaturan</a> dan memilih untuk berhenti berbagi data. <a href=\"https://woocommerce.com/usage-tracking\" target=\"_blank\">Baca selengkapnya</a> tentang data yang kami kumpulkan.', '{}', 'unactioned', 'woocommerce-admin', '2020-09-10 06:00:44', NULL, 0, 'plain', '', 0, 'info');
